import React, {useEffect} from 'react'
import './AboutMe.css'
import ScreenHeading from '../../utilities/ScreenHeading/ScreenHeading'
import ScrollService from '../../utilities/ScrollService'
import Animations from '../../utilities/Animations'
export default function AboutMe(props) {
    let fadeInScreenHandler = (screen)  => {
        if(screen.fadeScreen !== props.id)
            return
        Animations.animations.fadeInScreen(props.id)
    }
    const fadeInSubscription = ScrollService.currentScreenFadeIn.subscribe(fadeInScreenHandler)
    const SCREEN_CONSTANT = {
        description: "I am a current Software engineering student at Rochester Institute of Technology. With industry experience building web applications for Chick-Fil-A. My skillset runs very deep. I have worked various different projects: online stores, foundations, web applications involving trackers, portfolios, music players, etc... Whatever your project consists of, I know that I will be able to develop it in a timely fashion.I specialize in making websites, however, I also have a vast sense in UI/UX design. ",
        highlights: {
            bullets: [
                'Full Stack Web Engineering',
                'Interactive Front-end',
                'Cloud Computing', 
                'Developing React Apps',
                'Building REST APIs',
                'Managing Databases',
                'IoT & Blockchain'
            ],
            heading: "Here are a few highlights... "
        }
    }
    useEffect(() => {
        return () => {
          /* UNSUBSCRIBE THE SUBSCRIPTIONS */
          fadeInSubscription.unsubscribe();
        };
      }, [fadeInSubscription]);

    const renderHighlights = () => {
        return(SCREEN_CONSTANT.highlights.bullets.map((highlight, i) => (
            <div className="highlight" key={i}>
                <div className="highlight-blob"></div>
                <span> {highlight} </span>
            </div>
        )))
    }
    return (
        <div className="about-me-container" id={props.id || ""}>
            <div className="about-me-parent">
                <ScreenHeading title={'About Me'} subHeading={'I am your guy'} />
                <div className="about-me-card">
                    <div className="about-me-profile"></div>
                    <div className="about-me-details">
                        <span className="about-me-descripption"> {SCREEN_CONSTANT.description} </span>
                        <div className="about-me-highlights"> 
                            <div className="highlight-heading"> 
                                {SCREEN_CONSTANT.highlights.heading}
                            </div>
                            {renderHighlights()}
                        </div>
                        <div className="about-me-options"> 
                            <button className="btn primary-btn"> Hire Me </button>
                            <a href="Wikina_Gideon_Resume_Software.pdf" download="Wikina_Gideon_Resume_Software.pdf">
                                <button className="btn highlighted-btn"> Get Resume </button>
                            </a>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
